import React, { useState } from "react";
import { BrowserRouter as router, Route, Link } from 'react-router-dom';
import { Form, FormGroup, Dropdown, Input, Card, CardImg, DropdownToggle } from "reactstrap";

const [DropDownOpen, setDropDownOpen] = useState(false);
const toggle = () => setDropDownOpen((prevState) => !prevState);


const OrderForm = () => {
    return (
        <>
            <Card color='info'>
                <h2 style={{ color: 'white', margin: '0 auto' }}>
                    Build Your Own Taco!
                </h2>
                <CardImg style={{ width: '80%', margin: '0 auto' }} src={require('./assets/taco-2.jpg')} />
            </Card>
            <Form style={{ margin: '5%' }}>
                <FormGroup>
                    <legend>Name</legend>
                    <Input type='name' name='name' />
                </FormGroup>
                <FormGroup>
                    <Dropdown isOpen={DropDownOpen} toggle={toggle}>
                        <DropdownToggle caret>
                            Dropdowntoggle stuff
                        </DropdownToggle>

                    </Dropdown>
                </FormGroup>
            </Form>
        </>
    )
};

export default OrderForm;
